mapper.update:
update draft_line
set rec_check = '1'
where draft_no = #{draft_no}

update draft
set rec_date = to_char(sysdate, 'yyyy-mm-dd')
where draft_no = #{draft_no}

update draft
set rec_del_check = '1'
where draft_no = #{draft_no}

update draft
set del_check = '1'
where draft_no = #{draft_no}

update draft
set step_no = #{param1}
where draft_no = #{param2}
