mapper.update:
update mail
set sign = #{sign}
where mail_no = #{mail_no}

update mail
set rec_check ='1', rec_date = to_char(sysdate, 'yyyy-mm-dd')
where mail_no = #{mail_no}

update mail
set rec_del_check = '1'
where mail_no = #{mail_no}

update mail
set del_check = '1'
where mail_no = #{mail_no}

update mail
set rec_del_check = '0'
where mail_no = #{mail_no}
