mapper.select:
select rn, pj_no, emp_no, emp_name, pj_title, pj_start, pj_end, step_no from (select rownum rn, pj_no, p.emp_no, e.emp_name, pj_title, pj_start, pj_end, step_no
from pj p join emp e on p.emp_no = e.emp_no
where rec_emp_no =#{param1} and  rec_del_check = '0' and step_no='4') where rn between #{param2} and #{param3}
